import React from 'react';
import './index.css'

export default class CatGenerator extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
          catURL: ""
        };
        this.newCat = this.newCat.bind(this);
      }

    render() {
        return (
            <div className="cat-container">
                <img variant="top" src={this.state.catURL} />
                <button className="cat-button"onClick={this.newCat}>new cat gif</button>
            </div>
        );
    }

    componentDidMount() {
        // Simple GET request using fetch
        fetch('https://api.thecatapi.com/v1/images/search')
            .then(response => response.json())
            .then(data => this.setState({ catURL: data[0].url }));
    }

  newCat() {
    fetch('https://api.thecatapi.com/v1/images/search')
    .then(response => response.json())
    .then(data => this.setState({ catURL: data[0].url }));
    }
}